window.onscroll=function(){scrollFunction()};function scrollFunction(){if(document.body.scrollTop>90||document.documentElement.scrollTop>50){document.getElementById("bottom-nav").style.bottom="0"}else{document.getElementById("bottom-nav").style.bottom="-130px"}var b=document.body.scrollTop||document.documentElement.scrollTop;var a=document.documentElement.scrollHeight-document.documentElement.clientHeight;var c=(b/a)*100;document.getElementById("myBar").style.width=c+"%"};

            $(document).ready(function(){
                $('.a-inh-sup').click(function(){
                   var footnotesNumber = $(this).data('id');
                   // AJAX request
                   $.ajax({
                    url: '_beta_ajaxfile.php',
                    type: 'get',
                    data: {footnotesNumber: footnotesNumber},
                    success: function(response){ 
                      // Add response in Modal body
                      $('.modal-body').html(response);
                      // Display Modal
                      $('#footnotesModal').modal('show'); 
                    }
                  });
             });
            });

            //Back to Top Function
            $(window).scroll(function(){
              // console.log('wwww');
              if ($(this).scrollTop() > 400) {
                $('.top-scroller').fadeIn();
              } else {
                $('.top-scroller').fadeOut();
              }
            });
            //Click event to scroll to top
            $('.top-scroller').click(function(){
              $('html, body').animate({scrollTop : 0},100);
              return false;
            });
            
            
            



